mapper.select:

select

  distinct


from blc_customer_relation




  order by ${orderByClause}


  limit #{offset},#{limit}

select
count(case when customer_type = 1 then 1 else null end) as totalStoreNum, count(case when customer_type = 3 then 1 else null end) as totalVipNum
count(case when customer_type = 1 then 1 else null end) as monthNewStoreNum, count(case when customer_type = 3 then 1 else null end) as monthNewVipNum
count(case when customer_type = 1 then 1 else null end) as dayNewStoreNum, count(case when customer_type = 3 then 1 else null end) as dayNewVipNum
from blc_customer_relation where store_super_mgr_id = #{managerId}
and DATE_FORMAT(str_to_date(modify_date, '%Y-%m-%d'),'%Y%m') = #{month}
and DATE_FORMAT(str_to_date(modify_date, '%Y-%m-%d'),'%Y%m%d') = #{day}

select r.store_mgr_id as storeId, c.real_name as storeName

  count(1) as monthNewVipNum


  count(1) as dayNewVipNum

from blc_customer_relation r, blc_customer c
where r.store_mgr_id = c.customer_id and r.customer_type = 3 and r.store_mgr_id in

  #{item}


  and DATE_FORMAT(str_to_date(r.modify_date, '%Y-%m-%d'),'%Y%m') = #{month}


  and DATE_FORMAT(str_to_date(r.modify_date, '%Y-%m-%d'),'%Y%m%d') = #{day}

group by r.store_mgr_id

select c.CUSTOMER_ID as storeId, c.REAL_NAME as storeName,
count(case when DATE_FORMAT(str_to_date(r.modify_date, '%Y-%m-%d'),'%Y%m%d') = #{day} then 1 else null end) as dayNewVipNum
count(1) as monthNewVipNum from blc_customer_relation r, blc_customer c where r.store_mgr_id = c.CUSTOMER_ID and r.store_super_mgr_id = #{managerId} and r.customer_type = 3 and DATE_FORMAT(str_to_date(r.modify_date, '%Y-%m-%d'),'%Y%m') = #{month} GROUP BY c.HIGHER_CUSTOMER_ID order by count(1) desc limit #{page.offset}, #{page.limit}
