mapper.select:

select

  distinct


from commission_settlement_detail




  order by ${orderByClause}


  limit #{offset},#{limit}

select count(1)
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn
 and s.user_id = #{userId}
 and s.store_master_id = #{userId}
 and s.store_manage_id = #{userId}
 and s.store_id = #{userId}
and DATE_FORMAT(s.order_create_time,'%Y%m') = #{month}

  and d.`status` = #{subOrderStatus}

select DATE_FORMAT(s.order_create_time,'%Y%m') as month,s.order_create_time as orderCreateTime,d.item_price*d.buy_num as subOrderPrice,
d.order_sn as orderSn, d.item_name as itemName, d.`status` as subOrderStatus,
d.store_master_commission as storeMasterCommission,d.store_manage_commission as storeManagerCommission,

    (case when (( s.belong_type = 2 and (s.warn_status = 9 or s.biz_user_id is null)) or s.belong_type = 1)
    then '手工结算' else '自动结算' end) as settleType,

d.store_commission as storeCommission, d.company_commission as companyCommission
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn
 and s.user_id = #{queryDto.userId}
 and s.store_master_id = #{queryDto.userId}
 and s.store_manage_id = #{queryDto.userId}
 and s.store_id = #{queryDto.userId}
and DATE_FORMAT(s.order_create_time,'%Y%m') = #{queryDto.month}

  and d.`status` = #{queryDto.subOrderStatus}

order by s.order_create_time desc , s.order_sn asc

  limit #{offset}, #{limit}
