mapper.select:

select

  distinct


from commission_settlement




  order by ${orderByClause}


  limit #{offset},#{limit}

select
 ifnull(sum(d.store_master_commission),0) as masterToBeIncome
 ifnull(sum(d.store_manage_commission),0) as managerToBeIncome
 ifnull(sum(d.store_commission),0) as storeToBeIncome
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn and d.`status`=1
 and s.store_master_id = #{userId}
 and s.store_manage_id = #{userId}
 and s.store_id = #{userId}

select
 ifnull(sum(d.store_master_commission),0) as masterToBeIncome
 ifnull(sum(d.store_manage_commission),0) as managerToBeIncome
 ifnull(sum(d.store_commission),0) as storeToBeIncome
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn and d.`status`=2
and DATE_FORMAT(s.settlement_end_time,'%Y%m') = #{month}
 and s.store_master_id = #{userId}
 and s.store_manage_id = #{userId}
 and s.store_id = #{userId}

select ifnull(sum(d.buy_num * d.item_price),0) as monthTotalSales,
 ifnull(sum(d.store_master_commission),0) as monthTotalIncome
 ifnull(sum(d.store_manage_commission),0) as monthTotalIncome
 ifnull(sum(d.store_commission),0) as monthTotalIncome
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn and d.`status` in (1,2)
and DATE_FORMAT(s.order_create_time,'%Y%m') = #{month}
 and s.store_master_id = #{userId}
 and s.store_manage_id = #{userId}
 and s.store_id = #{userId}

select
ifnull(sum(case when d.status in (1,2) then d.buy_num * d.item_price else 0 end),0) as monthTotalSales,
ifnull(sum(case when d.status = 3 then d.buy_num * d.item_price else 0 end),0) as monthRefundSales,
ifnull(sum(case when d.status in (1,2) then d.buy_num * d.item_price else 0 end),0)/ifnull(sum(d.buy_num * d.item_price),0) as monthSalesRate,
ifnull(sum(case when d.status = 3 then d.buy_num * d.item_price else 0 end),0)/ifnull(sum(d.buy_num * d.item_price),0) as monthRefundRate
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn
and DATE_FORMAT(s.order_create_time,'%Y%m') = #{month}
 and d.brand_type = #{brandType}

 and s.store_master_id = #{userId}
 and s.store_manage_id = #{userId}
 and s.store_id = #{userId}

select ifnull(sum(d.buy_num * d.item_price),0) as sales
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn and d.`status` in (1,2)
and DATE_FORMAT(s.order_create_time,'%Y%m%d') = #{day}
 and d.brand_type = #{brandType}

 and s.store_master_id = #{userId}
 and s.store_manage_id = #{userId}
 and s.store_id = #{userId}

select ifnull(sum(store_master_commission),0)
from commission_settlement
where date_format(order_create_time, '%Y%m') = #{month}
and store_master_id in

  #{masterId}

select ifnull(sum(store_manage_commission),0)
from commission_settlement
where date_format(order_create_time, '%Y%m') = #{month}
and store_manage_id in

  #{managerId}

select ifnull(sum(ifnull(d.store_master_commission,0)),0) as keeperAvailableIncome,
ifnull(sum(ifnull(d.store_manage_commission,0)),0) as managerAvailableIncome,
ifnull(sum(ifnull(d.store_commission,0)),0) as directorAvailableIncome,
ifnull(sum(ifnull(d.store_master_commission,0) + ifnull(d.store_manage_commission,0) + ifnull(d.store_commission,0)),0) as storeAvailableIncome
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn and d.`status`=2
and DATE_FORMAT(s.settlement_end_time,'%Y%m') = #{month}
and s.store_id = #{userId}
 and d.brand_type = #{brandType}

select
ifnull(sum(ifnull(d.store_master_commission,0)),0) as keeperTotalIncome,
ifnull(sum(ifnull(d.store_manage_commission,0)),0) as managerTotalIncome,
ifnull(sum(ifnull(d.store_commission,0)),0) as directorTotalIncome,
ifnull(sum(ifnull(d.store_master_commission,0) + ifnull(d.store_manage_commission,0) + ifnull(d.store_commission,0)),0) as storeTotalIncome
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn and d.`status` in (1,2)
and DATE_FORMAT(s.order_create_time,'%Y%m') = #{month}
and s.store_id = #{userId}
 and d.brand_type = #{brandType}

select
ifnull(sum(ifnull(d.store_master_commission,0)),0) as keeperTotalIncome,
ifnull(sum(ifnull(d.store_manage_commission,0)),0) as managerTotalIncome,
ifnull(sum(ifnull(d.store_commission,0)),0) as directorTotalIncome,
ifnull(sum(ifnull(d.store_master_commission,0) + ifnull(d.store_manage_commission,0) + ifnull(d.store_commission,0)),0) as storeTotalIncome
from commission_settlement s, commission_settlement_detail d
where s.order_sn = d.order_sn and d.`status` in (1,2)
and DATE_FORMAT(s.order_create_time,'%Y%m%d') = #{day}
and s.store_id = #{userId}
 and d.brand_type = #{brandType}

select
  ifnull(sum(s.store_commission),0)
from commission_settlement s
where  s.status = 2 and (( s.belong_type = 2 and (s.ext_status = 1 or s.warn_status = 9 or s.biz_user_id is null))
or (s.belong_type = 1)
)
and s.store_id = #{userId}
  and DATE_FORMAT(s.order_create_time,'%Y%m') = #{month}
